import React, { useState } from 'react';
import './App.css';

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [startTime, setStartTime] = useState({ hour: '', period: '' });
  const [endTime, setEndTime] = useState({ hour: '', period: '' });
  const [weatherData, setWeatherData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false)

  // format date to YYYY-MM-DD
  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toISOString().split('T')[0];
  };

  // const formatDate = (dateStr) => {
  //   const date = new Date(dateStr);
  //   const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
  //   return date.toLocaleDateString('en-CA', options).replace(/\//g, '-');
  // };

  // convert time to 00:00:00
  const convertTimeTo24HourFormat = (timeObj) => {
    let { hour, period } = timeObj;
    hour = parseInt(hour);

    if (period === 'PM' && hour !== 12) {
      hour += 12;
    } else if (period === 'AM' && hour === 12) {
      hour = 0;
    }

    // return `${hour.toString().padStart(2, '0')}:00:00-05:00`;
    return `${hour.toString().padStart(2, '0')}:00:00`;
  };




   // Function to generate dates for the next seven days
   const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
   const nextSevenDays = Array.from({ length: 7 }, (_, index) => {
    const today = new Date();
     const date = new Date(today);
     date.setDate(date.getDate() + index);
     const dayOfWeek = daysOfWeek[date.getDay()];
     const month = date.toLocaleString('en-US', { month: 'short' });
     const day = date.getDate();
     const year = date.getFullYear();
     return `${dayOfWeek} ${month} ${day}, ${year}`;
   });

     // Arrays for hours and AM/PM
    const hours = Array.from({ length: 12 }, (_, i) => i + 1);
    const periods = ['AM', 'PM'];


  const handleSubmit = async (e) => {
    e.preventDefault();

     // Set isLoading to true when the form is submitted
     setIsLoading(true);

    const lat = parseFloat(latitude);
    const long = parseFloat(longitude);

    if (!latitude || !longitude) {
      setError('Please enter latitude and longitude');
      return;
    }

    setError('');

    const url = `https://api.weather.gov/points/${lat},${long}`;

    try {
      // 1ST API CALL TO GET HOURLY FORECAST ENDPOINT
      const response = await fetch(url);
      console.log("response", response)
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${response.status}`);
      }
      const data = await response.json();
      const forecastHourlyURL = data.properties.forecastHourly;

      // 2ND API CALL TO GET HOURLY FORECAST
      const forecastResponse = await fetch(forecastHourlyURL)
      console.log("forecast", forecastResponse)
      if (!forecastResponse.ok) {
        const errorData = await forecastResponse.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${forecastResponse.status}`);
      }

      const forecastData = await forecastResponse.json();
      setWeatherData(forecastData);

     // convert user input dates and times to match API data's startTime, endTime values
     const startDateTime = `${formatDate(startDate)}T${convertTimeTo24HourFormat(startTime)}`;
     const endDateTime = `${formatDate(endDate)}T${convertTimeTo24HourFormat(endTime)}`;
    // const startDateTime = new Date(`${formatDate(startDate)}T${convertTimeTo24HourFormat(startTime)}Z`);
    // const endDateTime = new Date(`${formatDate(endDate)}T${convertTimeTo24HourFormat(endTime)}Z`);
    // const startDateTime = new Date(`${formatDate(startDate)}T${convertTimeTo24HourFormat(startTime)}Z`).toISOString();
    // const endDateTime = new Date(`${formatDate(endDate)}T${convertTimeTo24HourFormat(endTime)}Z`).toISOString();

     console.log("datetime", endDateTime)
     console.log("format", formatDate(startDate))

     // filter data based on start date/time and end date/time
     const filteredPeriods = forecastData.properties.periods.filter(period => {
        const periodStartTime = deleteTimeZoneFromForecastTime(period.startTime);
        console.log("perioddd",periodStartTime)
        // console.log(new Date(period.startTime).getTime())
        // const periodStartTime = new Date(period.startTime).getTime();
        // const periodEndTime = new Date(period.endTime).getTime();
        // return (periodStartTime >= startDateTime && periodEndTime <= endDateTime.getTime());
        // console.log("iso", new Date(periodStartTime).toISOString())
        return (periodStartTime >= startDateTime && periodStartTime <= endDateTime);
     });

    //  const filteredPeriods = forecastData.properties.periods.filter(period => {
    //   const periodStartTime = new Date(period.startTime).toISOString();
    //   const periodEndTime = new Date(period.endTime).toISOString();
    //   console.log("period", periodEndTime)
    //   return (periodStartTime >= startDateTime && periodEndTime <= endDateTime);
    // });

    console.log("filters", filteredPeriods)

     setFilteredData(filteredPeriods);

    } catch (error) {
      console.log(error)
      setError(error.message);
      setWeatherData(null);
    }
  };

  const deleteTimeZoneFromForecastTime = (dateTime) => {
    const lastIndex = dateTime.lastIndexOf('-');

    const date = dateTime.substring(0, lastIndex);
    // Extract the substring before the last dash ("-")
    return date

  }

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);

   const newDate =  new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      weekday: 'short',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true
    }).format(date);

    return newDate
  }

  console.log(filteredData)
  console.log("orig", startTime)
  console.log(convertTimeTo24HourFormat(endTime))
  console.log(startTime)


  return (
    <div className="App">
      <h1>How's the weather? 🌡️</h1>
      <h3>Enter location coordinates</h3>
      <form onSubmit={handleSubmit}>
        <div className='coordinates-container'>
          <div className='latitude'>
            <label>
              Latitude:
              <input
                type="text"
                value={latitude}
                onChange={(e) => setLatitude(e.target.value)}
                placeholder="e.g., 36.1716"
              />
            </label>
          </div>
          <div className='longitude'>
            <label>
              Longitude:
              <input
                type="text"
                value={longitude}
                onChange={(e) => setLongitude(e.target.value)}
                placeholder="e.g., -115.1391"
              />
            </label>
          </div>
        </div>
        <h3>Enter time frame</h3>
        <div className='start-container'>
          <label htmlFor="startDate">Start Date:</label>
          <select id="startDate" value={startDate} onChange={(e) => setStartDate(e.target.value)}>
          <option value="" disabled>Select day</option>
            {nextSevenDays.map((date, index) => (
              <option key={index} value={date}>{date}</option>
            ))}
          </select>
        <label htmlFor="startTimeHour">Start Time:</label>
        <select id="startTimeHour" value={startTime.hour} onChange={(e) => setStartTime({ ...startTime, hour: e.target.value })}>
          <option value="" disabled>Select hour</option>
          {hours.map((hour, index) => (
            <option key={index} value={hour}>{hour}</option>
          ))}
        </select>
        <select id="startTimePeriod" value={startTime.period} onChange={(e) => setStartTime({ ...startTime, period: e.target.value })}>
          <option value="" disabled>Select period</option>
          {periods.map((period, index) => (
            <option key={index} value={period}>{period}</option>
          ))}
        </select>
      </div>
      <div className='end-container'>
      <label htmlFor="endDate">End Date:</label>
      <select id="endDate" value={endDate} onChange={(e) => setEndDate(e.target.value)}>
      <option value="" disabled>Select day</option>
        {nextSevenDays.map((date, index) => (
          <option key={index} value={date}>{date}</option>
        ))}
      </select>
      <label htmlFor="endTimeHour">End Time:</label>
      <select id="endTimeHour" value={endTime.hour} onChange={(e) => setEndTime({ ...endTime, hour: e.target.value })}>
        <option value="" disabled>Select hour</option>
        {hours.map((hour, index) => (
          <option key={index} value={hour}>{hour}</option>
        ))}
      </select>
      <select id="endTimePeriod" value={endTime.period} onChange={(e) => setEndTime({ ...endTime, period: e.target.value })}>
        <option value="" disabled>Select period</option>
        {periods.map((period, index) => (
          <option key={index} value={period}>{period}</option>
        ))}
      </select>
      </div>
        <button type="submit">Submit</button>
      </form>
      {error && <p className='error'>{error}</p>}
      {isLoading ? (
      <div>Loading...</div>
    ) : (
          <div>
          <h2>Forecast</h2>
          {filteredData.map((period, index) => (
            <div key={index} className='weather-container'>
              <h3>{formatDateTime(deleteTimeZoneFromForecastTime(period.startTime))}</h3>
               {/* <h4>Start Time: {new Intl.DateTimeFormat('en-US', { timeZone: 'UTC', weekday: 'short', month: 'short', day: 'numeric', year: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true }).format(new Date(period.startTime))} </h4> */}
              {/* <h4>Start Time:  {new Date(period.startTime).toLocaleString('en-US', { timeZone: 'UTC', weekday: 'short', month: 'short', day: 'numeric', year: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true })}</h4> */}
              {/* <h4>End Time: {new Date(period.endTime).toLocaleString('en-US', {  weekday: 'short', month: 'short', day: 'numeric', year: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true })}</h4> */}
              <p><b>{period.temperature} &deg;{period.temperatureUnit}</b></p>
              <p>{period.shortForecast}</p>
              <p>Precipitation: {period.probabilityOfPrecipitation.value}%</p>
            </div>
          ))}
        </div>
      )}

    </div>
  );
}

