import React, { useState } from 'react';
import './App.css';

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [hourlyForecastURL, setHourlyForecastURL] = useState('');
  const [hourlyData, setHourlyData] = useState(null);
  const [error, setError] = useState('');
  const [selectedDate, setSelectedDate] = useState('');

   // Function to generate dates for the next seven days
   const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
   const nextSevenDays = Array.from({ length: 7 }, (_, index) => {
    const today = new Date();
     const date = new Date(today);
     date.setDate(date.getDate() + index);
     const dayOfWeek = daysOfWeek[date.getDay()];
     const month = date.toLocaleString('en-US', { month: 'short' });
     const day = date.getDate();
     const year = date.getFullYear();
     return `${dayOfWeek} ${month} ${day}, ${year}`;
   });

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const lat = parseFloat(latitude);
    const long = parseFloat(longitude);

    if (!latitude || !longitude) {
      setError('Please enter latitude and longitude');
      return;
    }

    setError('');

    const url = `https://api.weather.gov/points/${lat},${long}`;

    try {
      const response = await fetch(url);
      console.log("response", response)
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${response.status}`);
      }

      const data = await response.json();
      setWeatherData(data);
      setHourlyForecastURL(data.properties.forecastHourly)

    } catch (error) {
      console.log(error)
      setError(error.message);
      setWeatherData(null);
    }
  };


  return (
    <div className="App">
      <h1>How's the weather?</h1>
      <h3>Enter location coordinates</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Latitude:
            <input
              type="text"
              value={latitude}
              onChange={(e) => setLatitude(e.target.value)}
              placeholder="e.g., 36.1716"
            />
          </label>
        </div>
        <div>
          <label>
            Longitude:
            <input
              type="text"
              value={longitude}
              onChange={(e) => setLongitude(e.target.value)}
              placeholder="e.g., -115.1391"
            />
          </label>
        </div>
        <label htmlFor="date">Select a date:</label>
      <select id="date" value={selectedDate} onChange={handleDateChange}>
        <option value="">Choose a date</option>
        {nextSevenDays.map((date, index) => (
          <option key={index} value={date}>
            {date}
          </option>
        ))}
      </select>
        <button type="submit">Submit</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {weatherData && (
        <div>
          <h2>Weather Data</h2>
          <pre>{JSON.stringify(weatherData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
