      <select id="end-time-hour" value={endTime.hour} onChange={(e) => setEndTime({ ...endTime, hour: e.target.value })}>
        <option value="" disabled>Select hour</option>
        {hours.map((hour, index) => (
          <option key={index} value={hour}>{hour}</option>
