import React, { useState } from 'react';
import './App.css';
import Form from './Form';
import WeatherForecast from './WeatherForecast';

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [startTime, setStartTime] = useState({ hour: '', period: '' });
  const [endTime, setEndTime] = useState({ hour: '', period: '' });
  const [filteredWeatherData, setFilteredWeatherData] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false)



  // convert time to 00:00:00, takes in startTime, endTime as timeObj
  const convertTimeTo24HourFormat = (timeObj) => {
    let { hour, period } = timeObj;
    hour = parseInt(hour);

    if (period === 'PM' && hour !== 12) {
      hour += 12;
    } else if (period === 'AM' && hour === 12) {
      hour = 0;
    }
    return `${hour.toString().padStart(2, '0')}:00:00`;
  };

   // Function to generate dates for the next seven days
   const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
   const nextSevenDays = Array.from({ length: 7 }, (_, index) => {
    const today = new Date();
     const date = new Date(today);
     date.setDate(date.getDate() + index);
     const dayOfWeek = daysOfWeek[date.getDay()];
     const month = date.toLocaleString('en-US', { month: 'short' });
     const day = date.getDate();
     const year = date.getFullYear();
     return `${dayOfWeek} ${month} ${day}, ${year}`;
   });

     // Arrays for hours and AM/PM
    const hours = Array.from({ length: 12 }, (_, i) => i + 1);
    const periods = ['AM', 'PM'];


  const handleSubmit = async (e) => {
    e.preventDefault();

    const lat = parseFloat(latitude);
    const long = parseFloat(longitude);

    if (!latitude || !longitude) {
      setError('Please enter latitude and longitude');
      setIsLoading(false); 
      return;
    }

    if (!startDate || !endDate || !startTime.hour || !startTime.period || !endTime.hour || !endTime.period) {
      setError('Please select date and/or time');
      setIsLoading(false);
      return;
    }

    setError('');
    setIsLoading(true);

    const url = `https://api.weather.gov/points/${lat},${long}`;

    try {
      // 1ST API CALL TO GET HOURLY FORECAST ENDPOINT
      const response = await fetch(url);
      console.log("response", response)
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${response.status}`);
      }
      const data = await response.json();
      const forecastHourlyURL = data.properties.forecastHourly;

      // 2ND API CALL TO GET HOURLY FORECAST
      const forecastResponse = await fetch(forecastHourlyURL)
      console.log("forecast", forecastResponse)
      if (!forecastResponse.ok) {
        const errorData = await forecastResponse.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${forecastResponse.status}`);
      }

      const forecastData = await forecastResponse.json();
      // setWeatherData(forecastData);

     // convert user input dates and times to match API data's startTime, endTime values
     const startDateTime = `${formatDate(startDate)}T${convertTimeTo24HourFormat(startTime)}`;
     const endDateTime = `${formatDate(endDate)}T${convertTimeTo24HourFormat(endTime)}`;

     console.log("datetime", endDateTime)
     console.log("format", formatDate(startDate))

     // filter data based on start date/time and end date/time
     const filteredPeriods = forecastData.properties.periods.filter(period => {
        const periodStartTime = deleteTimeZoneFromForecastTime(period.startTime);
        return (periodStartTime >= startDateTime && periodStartTime <= endDateTime);
     });

    console.log("filters", filteredPeriods)

     setFilteredWeatherData(filteredPeriods);
     setIsLoading(false);

    } catch (error) {
      console.log(error)
      setError(error.message);
      setFilteredWeatherData(null);
      setIsLoading(false);
    }
  };

  const deleteTimeZoneFromForecastTime = (dateTime) => {
    const lastIndex = dateTime.lastIndexOf('-');

    const date = dateTime.substring(0, lastIndex);
    // Extract the substring before the last dash ("-")
    return date

  }

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);

   const newDate =  new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      weekday: 'short',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true
    }).format(date);

    return newDate
  }

  return (
    <div className="App">
      <h1>How's the weather? 🌡️</h1>
      <Form  
        latitude={latitude}
        setLatitude={setLatitude}
        longitude={longitude}
        setLongitude={setLongitude}
        startDate={startDate}
        setStartDate={setStartDate}
        endDate={endDate}
        setEndDate={setEndDate}
        startTime={startTime}
        setStartTime={setStartTime}
        endTime={endTime}
        setEndTime={setEndTime}
        nextSevenDays={nextSevenDays}
        hours={hours}
        periods={periods}
        handleSubmit={handleSubmit}
      />

      {error && <p className='error'>{error}</p>}
      {isLoading ?  <h4><span className='loading-sun'>☀️</span> Loading...</h4> : 
      <WeatherForecast filteredWeatherData={filteredWeatherData} formatTimeZone={deleteTimeZoneFromForecastTime} formatDateTime={formatDateTime} />
      }
    </div>
  );
}

export default App;
