// WeatherForecast.js
import React from 'react';

const WeatherForecast = ({ filteredWeatherData, formatDateTime, formatTimeZone  }) => (
  <div>
    {filteredWeatherData.length > 0 ? (
      <>
        <h2>Weather Forecast</h2>
        {filteredWeatherData.map((period, index) => (
          <div className='weather-container'>
          <h3>{formatDateTime(formatTimeZone(period.startTime))}</h3>
          <p><b>{period.temperature} &deg;{period.temperatureUnit}</b></p>
          <p>{period.shortForecast}</p>
          <p>Precipitation: {period.probabilityOfPrecipitation.value}%</p>
        </div>
        ))}
      </>
    ) : (
      <h2>Please try again</h2>
    )}
  </div>
);

export default WeatherForecast;
