import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  // const [hourlyForecastURL, setHourlyForecastURL] = useState('');
  // const [hourlyData, setHourlyData] = useState(null);
  const [error, setError] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [startTime, setStartTime] = useState({ hour: '', period: '' });
  const [endTime, setEndTime] = useState({ hour: '', period: '' });
  const today = new Date();

  useEffect(() => {
    const formatDateWithDay = (date) => {
      const options = { weekday: 'short', month: 'short', day: 'numeric', year: 'numeric' };
      return date.toLocaleDateString('en-US', options);
    };

    const formatTime = (date) => {
      return date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true });
    };

    const defaultStartDate = formatDateWithDay(today);
    const defaultEndDate = formatDateWithDay(today);
    // const defaultStartTime = formatTime(today);
    // const defaultEndTime = formatTime(new Date(today.getTime() + 60 * 60 * 1000)); // 1 hour later

    setStartDate(defaultStartDate);
    setEndDate(defaultEndDate);
    // setStartTime(defaultStartTime);
    // setEndTime(defaultEndTime);
  }, [today]);

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toISOString().split('T')[0]; // "YYYY-MM-DD"
  };

  const formatTime = (timeStr) => {
    const [time, period] = timeStr.split(' ');
    let [hours, minutes] = time.split(':');
    minutes = minutes || '00';

    if (period.toLowerCase() === 'pm' && hours !== '12') {
      hours = (parseInt(hours) + 12).toString();
    } else if (period.toLowerCase() === 'am' && hours === '12') {
      hours = '00';
    }

    return `${hours}:${minutes}:00`; // "HH:MM:SS"
  };

   // Function to generate dates for the next seven days
   const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
   const nextSevenDays = Array.from({ length: 7 }, (_, index) => {
    const today = new Date();
     const date = new Date(today);
     date.setDate(date.getDate() + index);
     const dayOfWeek = daysOfWeek[date.getDay()];
     const month = date.toLocaleString('en-US', { month: 'short' });
     const day = date.getDate();
     const year = date.getFullYear();
     return `${dayOfWeek} ${month} ${day}, ${year}`;
   });

     // Arrays for hours and AM/PM
    const hours = Array.from({ length: 12 }, (_, i) => i + 1);
    const periods = ['AM', 'PM'];


  const handleSubmit = async (e) => {
    e.preventDefault();

    const lat = parseFloat(latitude);
    const long = parseFloat(longitude);

    if (!latitude || !longitude) {
      setError('Please enter latitude and longitude');
      return;
    }

    setError('');

    const url = `https://api.weather.gov/points/${lat},${long}`;

    try {
      const response = await fetch(url);
      console.log("response", response)
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${response.status}`);
      }

      const data = await response.json();
      const forecastHourlyURL = data.properties.forecastHourly;

      // 2ND API CALL TO GET HOURLY FORECAST
      const forecastResponse = await fetch(forecastHourlyURL)
      console.log("forecast", forecastResponse)
      if (!forecastResponse.ok) {
        const errorData = await forecastResponse.json();
        throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${forecastResponse.status}`);
      }

      const forecastData = await forecastResponse.json();
      setWeatherData(forecastData);

     // Convert user input dates and times
     const startDateTime = new Date(`${formatDate(startDate)}T${formatTime(startTime)}`);
     const endDateTime = new Date(`${formatDate(endDate)}T${formatTime(endTime)}`);


     const filteredPeriods = forecastData.properties.periods.filter(period => {
       const periodStartTime = new Date(period.startTime);
       const periodEndTime = new Date(period.endTime);
       return (periodStartTime >= startDateTime && periodEndTime <= endDateTime);
      });

      setFilteredData(filteredPeriods);


    } catch (error) {
      console.log(error)
      setError(error.message);
      setWeatherData(null);
    }
  };

  console.log(startDate, startTime)



  // const getHourlyWeather = async () => {
  //   console.log(hourlyForecastURL)
  //   try {
  //     const response = await fetch(hourlyForecastURL);
  //     console.log("response2", response)
  //     if (!response.ok) {
  //       const errorData = await response.json();
  //       throw new Error(`Error ${errorData.status}: ${errorData.detail}` || `Error: ${response.status}`);
  //     }

  //     const data = await response.json();
  //     setWeatherData(data);

  //   } catch (error) {
  //     console.log(error)
  //     setError(error.message);
  //     setWeatherData(null);
  //   }
  // }

  function formatDateString(inputDateStr) {
    // Parse the date string into a Date object
    const parsedDate = new Date(inputDateStr);

    // Extract the month, day, and year
    const month = parsedDate.getMonth() + 1; // Months are zero-indexed, so add 1
    const day = parsedDate.getDate();
    const year = parsedDate.getFullYear();

    // Format the date as "M/D/YYYY"
    return `${month}/${day}/${year}`;
  }


  return (
    <div className="App">
      <h1>How's the weather?</h1>
      <h3>Enter location coordinates</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Latitude:
            <input
              type="text"
              value={latitude}
              onChange={(e) => setLatitude(e.target.value)}
              placeholder="e.g., 36.1716"
            />
          </label>
        </div>
        <div>
          <label>
            Longitude:
            <input
              type="text"
              value={longitude}
              onChange={(e) => setLongitude(e.target.value)}
              placeholder="e.g., -115.1391"
            />
          </label>
        </div>
        <label htmlFor="startDate">Start Date:</label>
      <select id="startDate" value={startDate} onChange={(e) => setStartDate(e.target.value)}>
        {nextSevenDays.map((date, index) => (
          <option key={index} value={date}>{date}</option>
        ))}
      </select>
      <label htmlFor="endDate">End Date:</label>
      <select id="endDate" value={endDate} onChange={(e) => setEndDate(e.target.value)}>
        {nextSevenDays.map((date, index) => (
          <option key={index} value={date}>{date}</option>
        ))}
      </select>
      <label htmlFor="startTimeHour">Start Time:</label>
      <select id="startTimeHour" value={startTime.hour} onChange={(e) => setStartTime({ ...startTime, hour: e.target.value })}>
        <option value="" disabled>Select hour</option>
        {hours.map((hour, index) => (
          <option key={index} value={hour}>{hour}</option>
        ))}
      </select>
      <select id="startTimePeriod" value={startTime.period} onChange={(e) => setStartTime({ ...startTime, period: e.target.value })}>
        <option value="" disabled>Select period</option>
        {periods.map((period, index) => (
          <option key={index} value={period}>{period}</option>
        ))}
      </select>

      <label htmlFor="endTimeHour">End Time:</label>
      <select id="endTimeHour" value={endTime.hour} onChange={(e) => setEndTime({ ...endTime, hour: e.target.value })}>
        <option value="" disabled>Select hour</option>
        {hours.map((hour, index) => (
          <option key={index} value={hour}>{hour}</option>
        ))}
      </select>
      <select id="endTimePeriod" value={endTime.period} onChange={(e) => setEndTime({ ...endTime, period: e.target.value })}>
        <option value="" disabled>Select period</option>
        {periods.map((period, index) => (
          <option key={index} value={period}>{period}</option>
        ))}
      </select>
        <button type="submit">Submit</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {weatherData && (
        <div>
          <h2>Weather Data</h2>
          {/* <pre>{JSON.stringify(weatherData, null, 2)}</pre> */}
          {filteredData.map((period, index) => (
            <div key={index}>
              <p>Start Time: {period.startTime}</p>
              <p>End Time: {period.endTime}</p>
              <p>Temperature: {period.temperature} {period.temperatureUnit}</p>
              <p>Precipitation: {period.probabilityOfPrecipitation.value} {period.probabilityOfPrecipitation.unitCode}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
